(34, 1436464370, 'new', 'wfPluginUpgrade', 1, '71119f17e1660634a2e5ab17bceea3cf', '71119f17e1660634a2e5ab17bceea3cf', 'The Plugin "Adminer" needs an upgrade.', 'You need to upgrade "Adminer" to the newest version to ensure you have any security fixes the developer has released.', 'a:12:{s:4:"Name";s:7:"Adminer";s:9:"PluginURI";s:46:"http://bueltge.de/adminer-fuer-wordpress/1014/";s:7:"Version";s:5:"1.3.2";s:11:"Description";s:274:"<a href="http://www.adminer.org/en/">Adminer</a> (formerly phpMinAdmin) is a full-featured MySQL management tool written in PHP. This plugin include this tool in WordPress for a fast management of your database. <cite>By <a href="http://bueltge.de/">Frank Bültge</a></cite>";s:6:"Author";s:46:"<a href="http://bueltge.de/">Frank Bültge</a>";s:9:"AuthorURI";s:18:"http://bueltge.de/";s:10:"TextDomain";s:7:"adminer";s:10:"DomainPath";s:10:"/languages";s:7:"Network";b:0;s:5:"Title";s:68:"<a href="http://bueltge.de/adminer-fuer-wordpress/1014/">Adminer</a>";s:10:"AuthorName";s:13:"Frank Bültge";s:10:"newVersion";s:5:"1.4.2";}'),
